registrationModule.controller("mainController",function(t,n,a,o,i){n.showChat=0,t.idUsuario=3;var c=o.get("cita");t.descripcion=o.get("desc"),t.comentarios="",t.init=function(){t.cargaChat()},t.cargaChat=function(){i.getChat(c).then(function(n){n.data.length>0&&(t.chat=n.data)},function(t){})},t.EnviarComentario=function(n){i.putMessage(3,n,c).then(function(n){t.algo=n.data,t.clearComments(),t.cargaChat()},function(t){})},t.clearComments=function(){t.comentarios=""}});